	(178, 92, 'Scout Leadership', 'All Scouts you control can move an additional 2 spaces.'),
	(179, 92, 'Frenzy Enhancement', 'Add 1 to your die roll when you roll for the Frenzy power on any Army Card.'),
	(180, 92, 'Slither', 'Venoc Warlord does not have to stop his movement when entering water spaces.'),
	(183, 94, 'First Assault 3', 'When attacking with Valguard, if the defending figure was not adjacent to Valguard at the start of this turn, Valguard receives 3 additional attack dice.'),
